import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchPhotos } from '../../actions';
import { Link } from 'react-router-dom';

class PhotoList extends Component {
  componentDidMount() {
    // Fetch photos on component mount
    this.props.fetchPhotos();
  }

  renderPhotos = () => {
    // Render all photos as HTML
    return this.props.photos.map((photo) => {
      return <img key={photo.id} alt={photo.title} src={photo.url} />;
    });
  };

  renderCreateButton = () => {
    // If user is logged in, allow them to post new photo
    if (this.props.isSignedIn) {
      return (
        <Link to='/photos/new' className='ui primary button'>
          Post Photo
        </Link>
      );
    }
  };

  renderAdminControls = (photo) => {
    // Allow creator of photo to edit or delete photo
    if (photo.userId === this.props.currentUserId) {
      return (
        <Link to={`/photos/edit/${userId}`} className='ui button primary' />
      );
    }
  };

  render() {
    return (
      <div className='content ui container'>
        {this.renderCreateButton()}
        {this.renderPhotos()}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    photos: Object.values(state.photos),
    isSignedIn: state.auth.isSignedIn,
    currentUserId: state.auth.userId,
  };
};

export default connect(mapStateToProps, { fetchPhotos })(PhotoList);
