import * as type from '../actions/types';

const INITIAL_STATE = {
  hoverPhoto: null,
};

export default (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case type.HOVER_PHOTO:
      return { ...state, hoverPhoto: action.payload };
    case type.LEAVE_PHOTO:
      return { ...state, hoverPhoto: null };
    default:
      return state;
  }
};
