import React from 'react';
import GoogleAuth from './GoogleAuth';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import '../css/Header.css';
import styled from 'styled-components';

const SemanticNavbar = styled.div`
  margin: 8px 5px 0 5px;
  border: none;
`;

const SemanticInput = styled.div`
  background-color: #efefef;
  color: #757575;
  padding: 5px;
  border-radius: 5px;
  width: 50vw;
`;

const Header = (props) => {
  return (
    <SemanticNavbar className='ui secondary pointing menu'>
      <Link to='/' className='item'>
        <i className='pinterest icon big' />
      </Link>
      <Link to='/' className='item'>
        Home
      </Link>
      <div className='ui category search item'>
        <SemanticInput className='ui transparent icon input'>
          <div className='left aligned'>
            <i className='search link icon' />
          </div>
          <input className='prompt' type='text' placeholder='Search' />
        </SemanticInput>
      </div>
      {props.isSignedIn ? (
        <Link to='/photos/new' className='ui item'>
          Post Photo
        </Link>
      ) : null}

      <div className='right menu'>
        <GoogleAuth className='ui item' />
      </div>
    </SemanticNavbar>
  );
};

const mapStateToProps = (state) => {
  return {
    isSignedIn: state.auth.isSignedIn,
  };
};

export default connect(mapStateToProps)(Header);
