import * as type from '../actions/types';

export default (state = {}, action) => {
  switch (action.type) {
    case type.HOVER_PHOTO:
      return { ...state, isHoverPhoto: action.payload };
    case type.LEAVE_PHOTO:
      return { ...state, isHoverPhoto: null };
    default:
      return state;
  }
};
